import Layout from './Layout';
import ReactHLS from 'react-hls-player';
import Channels from './components/Channels';

let channels = {
    Ant1: "https://antennalivesp-lh.akamaihd.net/i/live_1@715138/index_800_av-p.m3u8"   
}




class Index extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            current: channels.ant1,
        }
        this.changeChannel = this.changeChannel.bind(this);
    }

    changeChannel = event => {
        let channelName = event.target.text;
        this.state = {current: channels.map(channel => {return channel === channelName})};
    }

    render() {
        return (
            <Layout>
                <h2>Availiable Channels</h2>
                <Channels />
                <div className="tv" ><ReactHLS url={this.state.current}/></div>
            </Layout>
        )
    }
}

export default Index;
