import ReactPlayer from 'react-player';
import styled from 'styled-components';

const StyledDiv = styled.div`
    width: 580px !important;
    margin: 0 auto !important;
`;

class MyPlayer extends React.Component {
    state = {
        url: null,
        playing: true,
        controls: false,
        volume: 0.8,
        muted: false,
        played: 0,
        loaded: 0,
    }

    load = () => {
        this.setState({
            url: this.props.url,
        })
    }

    handleLoad = url => {
        this.load(url);
    }

    handlePlay = () => {
        this.setState({ playing: true });
    }
    
    handlePause = () => {
        this.setState({ playing: false });
    }

    ref = player => {
        this.player = player
    }

    render() {
        const { url, playing, controls, volume, muted, played, loaded } = this.state;
        const SEPARATOR = ' · ';

        handleUrl = () => {
            this.props.url != ''? this.load() : ``;
        }

        return (
            <StyledDiv>
                {this.props.url}
                <ReactPlayer 
                    ref={this.ref}
                    className="react-player"
                    width='100%'
                    height='100%'
                    url={url}
                    playing={playing}
                    controls={controls}
                    volume={volume}
                    muted={muted}
                    onPlay={this.handlePlay}
                    onPause={this.handlePause}

                />
            </StyledDiv>
        )

    }
}

export default MyPlayer;
