import Layout from './Layout';
import ReactHLS from 'react-hls-player';

const myUrl = window.location.search;

let channels = {
    Ant1: "https://antennalivesp-lh.akamaihd.net/i/live_1@715138/index_800_av-p.m3u8",
    Alpha: "https://alphalive-i.akamaihd.net/hls/live/682300/live/high/prog_index.m3u8"  
}

class Player extends React.Component {
    constructor(props) {
        super(props);
        this.getChannel = this.getChannel.bind(this);
    }

    getChannel = () => {
        // return <p>this is the path {myUrl === '?channel=Ant1'? 'Ant1': 'blah'} </p>;
        return <p>this is the path {myUrl}</p>;
    }

    render(){
        return (
            <Layout>
                {this.getChannel()}
                {/* <ReactHLS url={this.getChannel} /> */}
            </Layout>
        )
    }
}

export default Player;
