import Layout from './Layout';
import ReactHLS from 'react-hls-player';
import Channels from './components/Channels';

let channels = {
    Ant1: "https://antennalivesp-lh.akamaihd.net/i/live_1@715138/index_800_av-p.m3u8",
    Alpha: "https://alphalive-i.akamaihd.net/hls/live/682300/live/high/prog_index.m3u8"  
}




class Index extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            current: "Ant1",
        }
        this.changeChannel = this.changeChannel.bind(this);
        this.getUrl = this.getUrl.bind(this);
    }

    changeChannel = event => {
        this.setState({
            current: event.target.textContent
        })
    }

    getUrl = () => {
        console.log(this.state.current);
    }

    render() {
        return (
            <Layout>
                <h2>Availiable Channels</h2>
                <Channels onCLick = {this.changeChannel}/>
                <button type="button" onClick={this.getUrl}>clickMe!</button>
                {/* <div className="tv" ><ReactHLS url={this.getUrl}/></div> */}
            </Layout>
        )
    }
}

export default Index;
