import Link from 'next/link';
import Layout from './Layout';
import styled from 'styled-components';
import NewPlayer from './components/NewPlayer';
import NavBar from './components/navbar';

let channels = {
    Ant1: "https://antennalivesp-lh.akamaihd.net/i/live_1@715138/index_800_av-p.m3u8",
    Alpha: "https://alphalive-i.akamaihd.net/hls/live/682300/live/high/prog_index.m3u8",
    Mega: "https://c98db5952cb54b358365984178fb898a.msvdn.net/live/S86713049/gonOwuUacAxM/playlist.m3u8",
    Star: "https://cdnapisec.siliconweb.com/p/713821/sp/0/playManifest/entryId/1_fp7fyi3j/format/applehttp/protocol/https/flavorParamId/0/manifest.m3u8",
    Skai: "https://youtu.be/CmqbY_Gzsdo",
    Ert1: "https://ert-live.siliconweb.com/media/ert_1/ert_1medium.m3u8",
    Ert2: "https://ert-live.siliconweb.com/media/ert_2/ert_2medium.m3u8",
    'Ert Sports': "https://ert-live.siliconweb.com/media/ert_sports/ert_sportshigh.m3u8",
}

const StyledDiv = styled.div`
    width: 580px !important;
    margin: 0 auto !important;
`;

export default class Channels extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            current: '',
            playerType: 0,
            oldState: ''
        }
        this.selectChannel = this.selectChannel.bind(this);
    }

    createButtons = items => {
        let names = Object.keys(channels);
        let buttons = [];
        names.forEach(key => {
            buttons.push(<button key={key} type="button" className="btn btn-outline-info mt-2 mr-2" onClick={this.selectChannel}>{key}</button>)
        });
        return buttons;
    }

    selectChannel = event => {
        this.setState({
            current: event.target.textContent,
            playerType: event.target.textContent === 'Skai'? 1:0
        })
    }

    playChannel = () => {
        if(this.state.current != '' && this.state.oldState === this.state.current) {
            return (
                <NewPlayer url = {channels[this.state.current]} playerType={this.state.playerType} />
            )
        }else {
            return <p className="text-center">Select a channel to stream.</p>
            this.setState({oldState: this.state.setState});
        }
    }

    render() {
        return (
            <Layout>     
                <StyledDiv>
                    {this.createButtons()}
                </StyledDiv>
                <hr className="hr"/>
                {this.playChannel()}
            </Layout>
        )
    }
}
