// import Link from 'next/link';
import Layout from './Layout';
import styled from 'styled-components';
// import NewPlayer from './components/NewPlayer';
// import MyPlayer from './components/MyPlayer';
// import NavBar from './components/navbar';

import ReactPlayer from 'react-player';

let channelNames = [
    {   name: 'Ant 1',
        url: "https://antennalivesp-lh.akamaihd.net/i/live_1@715138/index_800_av-p.m3u8"},
    {   name: 'Alpha',
        url: "https://alphalive-i.akamaihd.net/hls/live/682300/live/high/prog_index.m3u8"},
    {   name: 'Mega',
        url: "https://c98db5952cb54b358365984178fb898a.msvdn.net/live/S86713049/gonOwuUacAxM/playlist.m3u8"},
    {   name: 'Star',
        url: "https://cdnapisec.siliconweb.com/p/713821/sp/0/playManifest/entryId/1_fp7fyi3j/format/applehttp/protocol/https/flavorParamId/0/manifest.m3u8"},
    {   name: 'Skai',
        url: "https://youtu.be/CmqbY_Gzsdo"},
    {   name: 'Ert 1',
        url: "https://ert-live.siliconweb.com/media/ert_1/ert_1medium.m3u8"},
    {   name: 'Ert 2',
        url: "https://ert-live.siliconweb.com/media/ert_2/ert_2medium.m3u8"},
    {   name: 'Ert Sports',
        url: "https://ert-live.siliconweb.com/media/ert_sports/ert_sportshigh.m3u8"},
];

const StyledDiv = styled.div`
    width: 580px !important;
    margin: 0 auto !important;
`;

export default class Channels extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            activeChannel: '',
            // playerType: 0,
            // oldState: '',
            url: null,
            playing: true,
            controls: false,
            volume: 0.8,
            muted: false,
            played: 0,
            loaded: 0,
        }
        this.selectChannel = this.selectChannel.bind(this);
        this.playChannel = this.playChannel.bind(this);
        // this.handleLoad = this.handleLoad.bind(this);
    }

    // create the channel buttons function
    createButtons = () => {
        let buttons = [];
        channelNames.forEach(channel => {
            buttons.push(<button key={channel.name} type="button" className = "btn btn-outline-info mt-2 mr-2" onClick={this.selectChannel}>{channel.name}</button>)
        });
        return buttons;
    }

    // set active channel name
    selectChannel = event => {
        let name = event.target.textContent
        this.setState({
            activeChannel: event.target.textContent,
            url: channelNames.filter(channel => channel.name === name)
            // playerType: event.target.textContent === 'Skai'? 1:0
        })
    }

    // set channel url

    // handle new url
    // handleLoad = name => {
    //     let ch = channelNames.filter(channel => channel.name === name);
    //     return ch.url;
    // }
    // when paused

    // player reference

    // Player creation and use
    playChannel = () => {
        if(this.state.activeChannel != '') {
            return <p className = "text-center">the url is {this.state.url}</p>
            // return (<p className="text-center">{this.state.url}</p>)
            // <ReactPlayer 
            //     ref = {this.ref}
            //     className = "react-player"
            //     width = "100%"
            //     height = "100%"
            //     url = {this.handleLoad(this.state.url)}
            //     playing = {this.state.playing}
            //     controls = {this.state.controls}
            //     volume = {this.state.volume}
            //     muted = {this.state.muted} />
        }else {
            return <p className = "text-center">my nigga error!</p> 
            // return (<p className="text-center">Select a channel to stream.</p>) 
        }
        // if(this.state.activeChannel != '') {
        //     return (
        //         <p className="text-center">{this.state.activeChannel}</p>
        //         // <MyPlayer url = {channels[this.state.current]} />
        //         // <NewPlayer url = {channels[this.state.current]} playerType={this.state.playerType} />
        //     )
        // }else {
        //     return <p className="text-center">Select a channel to stream.</p>
        // }
    }

    render() {
        return (
            <Layout>     
                <StyledDiv>
                    {this.createButtons()}
                </StyledDiv>
                <hr className="hr"/>
                {this.playChannel()}
            </Layout>
        )
    }
}
